import './style.css';

function App() {
  return (
    <>
      <header className="header">
        <div className="logo">
          <img src="logo.png" />
          <h1>Share Your Route</h1>
        </div>
        <button className="btn btn-large btn-open">Share Your Route</button>
      </header>
      <NewFactsForm />
      <CategoriesAside />
    </>
  );

}
function CategoriesAside() {
  return (
    <aside className="categories-aside">
      <h2>Categories</h2>
      <ul className="categories-list">
        <li className="categories-list-item">
          <button className="categories-list-item-btn">On road</button>
        </li>
        <li className="categories-list-item">
          <button className="categories-list-item-btn">Off road</button>
        </li>
      </ul>
    </aside>
  );
}
function NewFactsForm() {
  return (
    <form className="route-form hidden">
      <h2>Share Your Route</h2>
      <input type="text" name="text" placeholder="Share your route" />
      <input type="text" name="source" placeholder="Source" />
      <input type="text" name="category" placeholder="Category" />
      <button type="submit">Share</button>
    </form>
  );
}
export default App;
// export default CategoriesAside;
