import './style.css';
import React, { useState } from 'react';
//Œ§Œ± Œ±œÅœáŒµŒπŒ± Œ≥ŒπŒ±œÑŒπ Œ¥ŒµŒΩ ŒµŒ∫Œ±ŒΩŒ± import 


const initialRoutes = [
  {
    id: 1,
    text: "I recently did the route from Tripoli to Levidi, Vytina, Dimitsana, Chrysovitsi, and Tripoli again, essentially it's the round of Menalo from there, and going up the ski lift you meet endless dirt roads, I'm attaching the following dirt roads in a gpx file for the friends)",
    source: "https://opensource.fb.com/",
    category: "on-bellow-400km",
    votesInteresting: 24,
    votesMindblowing: 9,
    votesFalse: 4,
    createdIn: 2021,
  },
  {
    id: 2,
    text: "a great way to spend your weekend especially direct guide dirt guides you will enjoy your bike very much on the olympus routes basically the climb consists of 75 km light dirt 100 km difficult and the second day is calculated around 65 km so you are the margin of return to Athens the link below is the ancient gpx",
    source:
      "https://www.mother.ly/parenting/millennial-dads-spend-more-time-with-their-kids",
    category: "on-above-400km",
    votesInteresting: 11,
    votesMindblowing: 2,
    votesFalse: 0,
    createdIn: 2019,
  },
  {
    id: 3,
    text: "good evening and from me, an excellent route for enduro friends is Mt. Ziria, it basically offers infinite paths of easy, difficult and extreme difficulty. I quote the maps that I followed coming from Athens, kissing in the cars in Kalavryta and going towards Mt. Ziria, the two routes are the first day e from Kalavryta to Mount Ziria and the second is the return essentially but from Tripoli heading south back to Athens",
    source: "https://en.wikipedia.org/wiki/Lisbon",
    category: "enduro-easy",
    votesInteresting: 8,
    votesMindblowing: 3,
    votesFalse: 1,
    createdIn: 2015,
  },
];

function App() {
  const [showForm, setShowForm] = useState(false);
  return (
    <>
      <Header showForm={showForm} setShowForm={setShowForm} />
      {showForm ? <NewRouteForm /> : null}
      <main class="main">
        <CategoriesAside />
        <RouteList />
      </main>
    </>
  );

}
function Header({ showForm, setShowForm }) {
  return (
    <header className="header">
      <div className="logo">
        <img src="logo.png" />
        <h1> Share Your Route</h1>
      </div>
      <button className="btn btn-large btn-open" onClick={() => setShowForm((show) => !showForm)}>
        {showForm ? "close" : "share your route"}
      </button>
    </header>
  );
}
const CATEGORIES = [
  { name: "on-bellow-400km", color: "#3b82f6" },
  { name: "on-above-400km", color: "#16a34a" },
  { name: "enduro-easy", color: "#ef4444" },
  { name: "enduro-medium", color: "#eab308" },
  { name: "enduro-hard", color: "#db2777" },
  { name: "on-off-easy", color: "#14b8a6" },
  { name: "on-off-medium", color: "#f97316" },
  { name: "on-off-hard", color: "#8b5cf6" },
];
function CategoriesAside() {
  return (
    <aside >
      <ul>
        <li className="category"><button className="btn btn-all-categories" >All</button></li>
        {CATEGORIES.map((category) => (
          <li key={category.name} className="category"> <button className="btn btn-category"
            style={{ backgroundColor: category.color }}>{category.name}</button>
          </li>))}
      </ul>
    </aside >

  );
}
function NewRouteForm() {
  const [text, setText] = useState("");
  const [source, setSource] = useState("");
  const [category, setCategory] = useState("");
  const textLength = text.length;
  function handleSubmit(e) {
    e.preventDefault();
    const newRoute = {
      id: routes.length + 1,
      text,
      source,
      category,
      votesInteresting: 0,
      votesMindblowing: 0,
      votesFalse: 0,
      createdIn: new Date().getFullYear(),
    };
    routes.push(newRoute);
    setText("");
    setSource("");
    setCategory("");
  }
  return (
    <form class="route-form " onSubmit={handleSubmit()}>
      {/* //changing the input value */}
      <input type="text" name="" id="" placeholder="Share your route" value={text}
        onChange={(e) => setText(e.target.value)}
      />
      <span>{200 - text.length}</span>
      <input type="text" name="" id="" value={source} placeholder="Google Maps" onChange={(e) => setSource(e.target.value)} />
      <select name="" id="" value={category} onChange={(e) => setCategory(e.target.value)}>
        <option value="">Choose a category</option>
        {CATEGORIES.map((category) => <option key={category.name} value={category.name} >{category.name.toUpperCase()}</option>)}
      </select>
      <button class="btn btn-large">Post </button>
    </form>
  );

}
function RouteList() {
  const routes = initialRoutes;
  return (
    //Reading facts from the array initialRoutes array
    <section > <ul className="routes-list">{
      //reading the array above and i use key  for the routes id because i want every key to be unique
      routes.map((route) => (<Route key={
        route.id
      } route={route} />

      ))
    }</ul>
      <p>There are {routes.length} different routes , chosse one of them and have fun  </p></section >
  );
}
function Route({ route }) {
  return (<li className="route">
    <p>{route.text}<a className="map" href={route.source} target="_blank">Route</a>
    </p>
    <span className="tag" style={{ backgroundColor: CATEGORIES.find((cat) => cat.name === route.category).color }}>
      {route.category}
    </span>
    <div className="vote-buttons">
      <button>üëç {route.votesInteresting} </button>
      <button>ü§Ø {route.votesMindblowing} </button>
      <button>‚õîÔ∏è {route.votesFalse} </button>
    </div>
  </li>);
}
export default App;
